///
/// 玩法组获取与更新服务，优先获取本地数据，本地不存在则从远程拉取；
/// 如果过期，优先使用过期数据，同时触发远程数据获取，并更新到本地
///

class PlayGroupServer{
  static PlayGroupServer ins = PlayGroupServer._();

  String _playGroupDirName;

  ///过期时间7天  7*24*60*60*1000
  int overdueInterval = 5*60*1000;//5分钟  /*1*1*60*60*1000;  //1小时*/


  Map<String,dynamic> cache = Map<String,dynamic>();

  PlayGroupServer._(){
    _init();
  }

  void init(){
    _init();
  }


  ///初始化文件存储目录
  void _init()async{
    String dirPath;
    if(Platform.isIOS){
      dirPath = (await getApplicationSupportDirectory()).path;
    } else {
      dirPath = (await getApplicationDocumentsDirectory()).path;
    }

    if(isNullOrEmpty(dirPath)){
      return;
    }

    String playGroupDirName = dirPath+"/"+"playgroup";
    Directory dir = Directory(playGroupDirName);
    if(!(await dir.exists())){
      dir.createSync();
    }
    _playGroupDirName = playGroupDirName;
  }


  ///
  Future<PlayGroupModel> getPlayGroup(String gameType,String gameTypeSon) async {
    String cacheKey = _getCacheKey(gameType, gameTypeSon);
    //从一级缓存中读取
    if(_localExist(gameType, gameTypeSon)){
      if(_isOverdue(gameType, gameTypeSon)){
        if(cache.containsKey(cacheKey)){
          return cache[cacheKey];
        } else {
          cache.clear();
        }
      }
    }

    var rst;
    if(_localExist(gameType, gameTypeSon)){
      if(_isOverdue(gameType, gameTypeSon)){
        Future.delayed(Duration(milliseconds: 10),()async{
          await _fromRemoteFetch(gameType, gameTypeSon);
        });
      }
      rst = await _fromLocalFetch(gameType,gameTypeSon);//从二级缓存-文件中读取
    } else {
      rst = await _fromRemoteFetch(gameType, gameTypeSon);//从三级缓存-通过网络获取
    }
    cache.clear();
    cache[cacheKey] = rst;
    return rst;
  }


  ///本地是否存在
  bool _localExist(String gameType,String gameTypeSon){
    String fileName = _getFilePath(gameType, gameTypeSon);
    File file = File(fileName);
    if(file.existsSync() && file.lengthSync() > 0){
      return true;
    }
    return false;
  }


  ///是否已经过期
  bool _isOverdue(String gameType,String gameTypeSon){
    String fileName = _getFilePath(gameType, gameTypeSon);
    File file = File(fileName);
    var dateTime = file.lastModifiedSync();
    if(DateTime.now().millisecondsSinceEpoch - dateTime.millisecondsSinceEpoch > overdueInterval){
      return true;
    }
    return false;
  }


  ///从本地获取玩法组数据
  Future<PlayGroupModel> _fromLocalFetch(String gameType,String gameTypeSon) async {
    try{
      String fileName = _getFilePath(gameType, gameTypeSon);
      File file = File(fileName);
      String content = file?.readAsStringSync();
      if(isNullOrEmpty(content)){
        return null;
      }
      return PlayGroupModel.fromMap(_convert.jsonDecode(content));
    }catch(_){
      return null;
    }
  }


  ///从远程获取玩法组数据
  Future<PlayGroupModel> _fromRemoteFetch(String gameType,String gameTypeSon) async {
    var playGroupParams = {'gameType': gameType};
    if(gameType == 'OP_DJ' && !isStrNullOrEmpty(gameTypeSon)){
      playGroupParams['cGameType'] = gameTypeSon;
    }
    var rsp;
    var resPlayGroup;
    try{
      rsp = await Net.getPlayGroupString(params:playGroupParams);
      resPlayGroup = PlayGroupModel.fromMap(rsp);
    } catch(_){}

    var data = resPlayGroup?.data;
    if(!(resPlayGroup?.isLogout ?? false)) {
      if((resPlayGroup?.isSuccess ?? false) && data != null && ((data.novice != null && data.novice.isNotEmpty) || (data.veteran != null && data.veteran.isNotEmpty))){
        String filePath = _getFilePath(gameType, gameTypeSon);
        if(!isNullOrEmpty(filePath)){
          _updateLocalData(filePath,_convert.jsonEncode(rsp));
        }
      } else if(!_localExist(gameType, gameTypeSon) && !isNullOrEmpty(LocalPlayGroup.getPlayGroup(gameType, gameTypeSon))){
        String filePath = _getFilePath(gameType, gameTypeSon);
        if(!isNullOrEmpty(filePath)){
          _updateLocalData(filePath,LocalPlayGroup.getPlayGroup(gameType, gameTypeSon));
          resPlayGroup = PlayGroupModel.fromMap(_convert.jsonDecode(LocalPlayGroup.getPlayGroup(gameType, gameTypeSon)));
        }
      }
    }
    return  resPlayGroup;
  }


  ///更新本地玩法组数据
  void _updateLocalData(String filePath,String data) async {
    try{
      File file = File(filePath);
      file.writeAsStringSync(data,mode: FileMode.writeOnly);
      //print("file.lastModifiedSync():${file.lastModifiedSync()}");
      file.lastModifiedSync();
      //print("file length:${await file.length()}");
      //print("read:"+file.readAsStringSync());
      //print("file.lastModifiedSync():${file.lastModifiedSync()}");
    } catch(e){
      print(e);
    }
  }


  ///文件路径
  String _getFilePath(String gameType,String gameTypeSon){
    if(isNullOrEmpty(_playGroupDirName)){
      return "";
    }
    return _playGroupDirName+'/$gameType'+'_'+'$gameTypeSon'+'_${Translations.langCode}'+'.json';
  }

  String _getCacheKey(String gameType,String gameTypeSon){
    return '$gameType'+'_'+'$gameTypeSon'+'_${Translations.langCode}';
  }

}


